import React from "react";

class Book extends React.Component {


  state = {
    showComments: false,
    comments: [],
    newComment: ""
  }


  handleAddComment = () => {
    this.setState({
      commentsForm: !this.state.commentsForm
    })
  }

  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  handleCommentSubmit = (e) => {
    // console.log("Comment: ", this.state.newComment)
    e.preventDefault()
    this.setState({
      comments: [...this.state.comments, this.state.newComment]
    })
    this.setState({
      newComment: ""
    })
  }

  handleShowComments = () => {
    console.log("comments: ", this.state.comments.join(". "))
    this.setState({
      allComments: !this.state.allComments
    })
  }


  addBookShelf = () => {
    this.props.clickHandler(this.props.book)
  }

  render(){

    const commentForm = 
      <div>  
        <textarea name = "newComment" placeholder = "Add a book comment..." value = {this.state.newComment} onChange = {this.handleChange}/>
        <br></br>
        <button onClick = {this.handleCommentSubmit}>Submit</button>
      </div>


    return (
      <div>
        <h2>{this.props.book.title}</h2>
        <img alt="" src={this.props.book.img} onClick={this.addBookShelf}/>
        <button onClick={() => {this.setState({showComments: !this.state.showComments})}}>Add Comment</button>
        <button>All comments</button>
        {this.state.showComments? <div><textarea placeholder="add comment here"> </textarea><button>submit comment</button></div> : null}
      </div>
    );
  }
};

export default Book;
